mapper.select:
	SELECT * FROM (
	    SELECT n.*, ROWNUM AS rnum FROM (
	        SELECT * FROM notice


                    ${requestData.column} LIKE '%' || #{requestData.keyword} || '%'


	        ORDER BY id DESC
	    ) n
	)
	WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE notice
SET title = #{notice.title},
    content = #{notice.content},
    notice_image = #{notice.noticeImage}
WHERE id = #{id}

mapper.select:
select * from (
	select notice.* , ROWNUM AS rn 
	from ( select * from notice



						where title like '%' || #{pageRequest.searchKeyword} || '%'


						where content like '%' || #{pageRequest.searchKeyword} || '%'


						where title like '%' || #{pageRequest.searchKeyword} || '%'
						or content like '%' || #{pageRequest.searchKeyword} || '%'



			order by created_at desc
		) notice
	where ROWNUM <= #{endRow}   
	)
 where rn >= #{startRow}

select count(*) from
notice
where category = #{category}

select * from (
	select event.* , ROWNUM AS rn 
	from event 
	where status = #{status}



		and category = #{category}

	and ROWNUM <= #{endRow}   
	)
 	where rn >= #{startRow}

	SELECT * FROM (
	    SELECT e.*, ROWNUM AS rnum FROM (
	        SELECT * FROM event


                    ${requestData.column} LIKE '%' || #{requestData.keyword} || '%'


	        ORDER BY id DESC
	    ) e
	) 
	WHERE rnum BETWEEN #{start} AND #{end}
