mapper.select:
	SELECT * FROM (
	    SELECT r.*, ROWNUM AS rnum FROM (
	        SELECT * FROM review


                    ${requestData.column} LIKE '%' || #{requestData.keyword} || '%'


	        ORDER BY id DESC
	    ) r
	)
	WHERE rnum BETWEEN #{start} AND #{end}

SELECT * 
FROM review 
WHERE member_id = #{memberId} 
  AND store_id = #{storeId} 
  AND ROWNUM = 1

mapper.update:
UPDATE review
SET status = 'hidden'
WHERE id = #{id}

UPDATE review
SET content = #{content}
WHERE id = #{id}
